Ext.define('sdkinte.view.app.region.AppGridToolbar',{
	extend :'Ext.toolbar.Toolbar',
	alias : 'widget.apppricetoolbar',
	controller : 'app',
	defaults:{
	margin:'0 10 0 10'
	},
	initComponent:function(){
		this.items=[{
			text : '新增',
			glyph : 0xf016,
			handler:'addAppprice'
			},{
			text : '修改',
			glyph : 0xf044,
			handler:'editAppprice'
			},{
			text : '删除',
			glyph : 0xf014,
			handler:'deleteAppprice'
			}];
		this.callParent();
	}
});